import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [itemText, setItemText] = useState('');
  const [listItems, setListItems] = useState([]);
  const [isUpdating, setIsUpdating] = useState('');
  const [updateItemText, setUpdateItemText] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [sortOption, setSortOption] = useState('');
  const [currentTab, setCurrentTab] = useState('all'); // Added tab state
  const [taskStats, setTaskStats] = useState({
    completedTasks: 0,
    onTimeCompletedTasks: 0,
  });

  // Add new todo item to database
  const addItem = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5500/api/item', {
        item: itemText,
        dueDate: dueDate,
      });
      setListItems((prev) => [...prev, res.data]);
      setItemText('');
      setDueDate('');
    } catch (err) {
      console.log(err);
    }
  };

  // Function to fetch todo items with sorting option
  const fetchTodoItems = async () => {
    try {
      let apiUrl = 'http://localhost:5500/api/items';
      if (sortOption) {
        apiUrl += `?sort=${sortOption}`;
      }
      const res = await axios.get(apiUrl);
      setListItems(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  // Create function to fetch all todo items from the database with sorting
  useEffect(() => {
    fetchTodoItems();
    fetchTaskStatistics();

  }, [sortOption]);

  const deleteItem = async (id) => {
    try {
      const res = await axios.delete(`http://localhost:5500/api/item/${id}`);
      const newListItems = listItems.filter((item) => item._id !== id);
      setListItems(newListItems);
    } catch (err) {
      console.log(err);
    }
  };

  const updateItem = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(`http://localhost:5500/api/item/${isUpdating}`, {
        item: updateItemText,
      });
      const updatedItemIndex = listItems.findIndex((item) => item._id === isUpdating);
      listItems[updatedItemIndex].item = updateItemText;
      setUpdateItemText('');
      setIsUpdating('');
    } catch (err) {
      console.log(err);
    }
  };

  const handleCheckboxChange = async (taskId, completed) => {
    try {
      const res = await axios.put(`http://localhost:5500/api/item/${taskId}`, {

        completed: completed,
        completedAt: completionTime
      });
      setListItems((prevList) =>
        prevList.map((item) =>
          item._id === taskId ? { ...item, completed: completed } : item
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  const renderUpdateForm = () => (
    <form className="update-form" onSubmit={(e) => updateItem(e)}>
      <input
        className="update-new-input"
        type="text"
        placeholder="New Item"
        onChange={(e) => setUpdateItemText(e.target.value)}
        value={updateItemText}
      />
      <button className="update-new-btn" type="submit">
        Update
      </button>
    </form>
  );

  // Filter tasks based on the current tab selection
  const filteredListItems = listItems.filter((item) => {
    if (currentTab === 'completed') {
      return item.completed;
    } else if (currentTab === 'notCompleted') {
      return !item.completed;
    }
    return true; // 'all' tab, so show all items
  });

  const isDueDateInFuture = (dueDate) => {
    const currentDateTime = new Date();
    const dueDateTime = new Date(dueDate);
    return dueDateTime > currentDateTime;
  };

  const fetchTaskStatistics = async () => {
    try {
      const res = await axios.get('http://localhost:5500/api/task-statistics');
      setTaskStats(res.data);
    } catch (err) {
      console.log(err);
    }
  };


  return (
    <div className="App">
      <h1>Todo List</h1>
      <div className="statistics">
  <h2>Task Statistics</h2>
  <p>Total Completed Tasks: {taskStats.completedTasks}</p>
  <p>Completed Tasks On Time: {taskStats.onTimeCompletedTasks}</p>
</div>

      <div className="sort-options">
        <label>
          Sort By:
          <select
            onChange={(e) => setSortOption(e.target.value)}
            value={sortOption}
          >
            <option value="">Time Created</option>
            <option value="title">Title</option>
            <option value="dueDate">Due Date</option>
          </select>
        </label>
      </div>
      <form className="form" onSubmit={(e) => addItem(e)}>
        <input
          type="text"
          placeholder="Add Todo Item"
          onChange={(e) => setItemText(e.target.value)}
          value={itemText}
        />
        <input
          type="date"
          placeholder="Due Date"
          onChange={(e) => setDueDate(e.target.value)}
          value={dueDate}
        />
        <button type="submit">Add</button>
      </form>
      <div className="tabs">
        <button onClick={() => setCurrentTab('all')}>All</button>
        <button onClick={() => setCurrentTab('completed')}>Completed</button>
        <button onClick={() => setCurrentTab('notCompleted')}>Not Completed</button>
      </div>
      <div className="todo-listItems">
        {filteredListItems.map((item) => (
          <div className={`todo-item ${isDueDateInFuture(item.dueDate) ? 'future' : 'past'}`} key={item._id}>
            {isUpdating === item._id ? (
              renderUpdateForm()
            ) : (
              <>
                <p className="item-content">{item.item}</p>
                <p className="item-content">Due Date: {new Date(item.dueDate).toLocaleDateString()}</p>
                <label>
                  Completed:
                  <input
                    type="checkbox"
                    checked={item.completed}
                    onChange={() => handleCheckboxChange(item._id, !item.completed)}
                  />
                </label>
                <button
                  className="update-item"
                  onClick={() => setIsUpdating(item._id)}
                >
                  Update
                </button>
                <button
                  className="delete-item"
                  onClick={() => deleteItem(item._id)}
                >
                  Delete
                </button>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
