import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [itemText, setItemText] = useState('');
  const [listItems, setListItems] = useState([]);
  const [isUpdating, setIsUpdating] = useState('');
  const [updateItemText, setUpdateItemText] = useState('');
  const [dueDate, setDueDate] = useState('');

  // Add new todo item to the database
  const addItem = async (e) => {
    e.preventDefault();
    try {
      console.log(dueDate)
      const res = await axios.post('http://localhost:5500/api/item', {
        item: itemText,
        dueDate: dueDate,
      });
      setListItems((prev) => [...prev, res.data]);
      setItemText('');
      setDueDate(''); // Clear the dueDate input
    } catch (err) {
      console.log(err);
    }
  }

  // Create function to fetch all todo items from the database
  useEffect(() => {
    const getItemsList = async () => {
      try {
        const res = await axios.get('http://localhost:5500/api/items')
        setListItems(res.data);
        console.log('render')
      } catch (err) {
        console.log(err);
      }
    }
    getItemsList();
  }, []);

  // Delete item when clicking on delete
  const deleteItem = async (id) => {
    try {
      const res = await axios.delete(`http://localhost:5500/api/item/${id}`)
      const newListItems = listItems.filter(item => item._id !== id);
      setListItems(newListItems);
    } catch (err) {
      console.log(err);
    }
  }

  // Update item
  const updateItem = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(`http://localhost:5500/api/item/${isUpdating}`, { item: updateItemText })
      console.log(res.data);
      const updatedItemIndex = listItems.findIndex(item => item._id === isUpdating);
      const updatedItem = listItems[updatedItemIndex].item = updateItemText;
      setUpdateItemText('');
      setIsUpdating('');
    } catch (err) {
      console.log(err);
    }
  }

  // Handle checkbox change to mark tasks as completed or not
  const handleCheckboxChange = async (taskId, completed) => {
    try {
      const res = await axios.put(`http://localhost:5500/api/item/${taskId}`, { completed });
      setListItems((prevList) =>
        prevList.map((item) =>
          item._id === taskId ? { ...item, completed } : item
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  // Before updating an item, show an input field for the updated item
  const renderUpdateForm = () => (
    <form className="update-form" onSubmit={(e) => { updateItem(e) }} >
      <input className="update-new-input" type="text" placeholder="New Item" onChange={e => { setUpdateItemText(e.target.value) }} value={updateItemText} />
      <button className="update-new-btn" type="submit">Update</button>
    </form>
  )

  return (
    <div className="App">
      <h1>Todo List</h1>
      <form className="form" onSubmit={e => addItem(e)}>
        <input type="text" placeholder='Add Todo Item' onChange={e => { setItemText(e.target.value) }} value={itemText} />
        <input
          type="date"
          placeholder="Due Date"
          onChange={(e) => setDueDate(e.target.value)}
          value={dueDate}
        />
        <button type="submit">Add</button>
      </form>
      <div className="todo-listItems">
        {
          listItems.map((item) => (
            <div className="todo-item" key={item._id}>
              {
                isUpdating === item._id
                  ? renderUpdateForm()
                  : (
                    <>
                      <p className="item-content">{item.item}</p>
                      <p className="item-content">Due Date: {formatDate(item.dueDate)}</p>

                      <input
                        type="checkbox"
                        checked={item.completed}
                        onChange={() => handleCheckboxChange(item._id, !item.completed)}
                      />
                      <button className="update-item" onClick={() => setIsUpdating(item._id)}>Update</button>
                      <button className="delete-item" onClick={() => deleteItem(item._id)}>Delete</button>
                    </>
                  )
              }
            </div>
          ))
        }
      </div>
    </div>
  );
}

function formatDate(date) {
  const options = {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    timeZone: 'UTC', // Set the time zone to 'UTC' or your preferred time zone
  };
  return new Date(date).toLocaleDateString(undefined, options);
}

export default App;
